mapper.select:
select T1.*, T2.staff_name, T3.nick_name as createByName, T5.project_name, T5.project_source

    , T4.working_hours as subPaidHours

from wh_task T1
left join wh_staff T2 on T1.staff_id = T2.id
left join sys_user T3 on T1.create_by = T3.user_name and T3.del_flag = '0'

    inner join wh_task_distribution T4 on T4.task_id = T1.id

left join wh_project T5 on T1.project_id = T5.id

    T1.del_flag = '0'
     and T1.parent_id = #{parentId}
     and T1.project_id = #{projectId}
     and T1.task_no like concat('%', #{taskNo}, '%')
     and T1.task_name like concat('%', #{taskName}, '%')
     and T1.app_name like concat('%', #{appName}, '%')
     and T1.distribution_time = #{distributionTime}
     and T1.participation_methods = #{participationMethods}
     and T1.month = #{month}
     and DATE_FORMAT(T1.distribution_time, '%Y-%m') = #{distributeMonth} 
     and T1.status = #{status}
     and T1.staff_id = #{staffId}
     and T1.distribute_status = #{distributeStatus}
     and T2.staff_name like concat('%', #{staffName}, '%' )
     and T3.nick_name like concat('%', #{createByName}, '%' )
     and T4.staff_name = #{salaryStaffName} 
     and T5.project_source = #{projectSource}

select T1.*, T2.staff_name, T3.nick_name as createByName, T5.task_no as parentTaskNo, T5.participation_methods as createParticipationMethods,
 T6.human_company_relation, T6.config_status, T7.main_project_name, T7.project_name

    , T4.working_hours as subPaidHours

from wh_task T1
left join wh_staff T2 on T1.staff_id = T2.id
left join sys_user T3 on T1.create_by = T3.user_name and T3.del_flag = '0'

    inner join wh_task_distribution T4 on T4.task_id = T1.id

left join wh_task T5 on T1.parent_id = T5.id
left join sys_user T6 on T2.staff_no = T6.user_name and T6.del_flag = '0'
left join wh_project T7 on T1.project_id = T7.id

    T1.del_flag = '0'
    and T1.distribute_status = 1
    and (T1.parent_id = 0 || T5.stage = 4)
     and T1.parent_id = #{parentId}
     and T7.main_project_name like concat('%', #{mainProjectName}, '%')
     and T1.project_id = #{projectId}
     and T1.project_type = #{projectType}
     and T1.task_no like concat('%', #{taskNo}, '%')
     and T1.task_name like concat('%', #{taskName}, '%')
     and T1.app_name like concat('%', #{appName}, '%')
     and T1.distribution_time = #{distributionTime}
     and T1.participation_methods = #{participationMethods}
     and T1.month = #{month}
     and DATE_FORMAT(T1.distribution_time, '%Y-%m') = #{distributeMonth} 
     and T1.status = #{status}
     and T1.create_by = #{createBy} 
     and T3.nick_name like concat('%', #{createByName}, '%' )
     and T2.staff_no like concat('%', #{staffNoQuery}, '%' )
     and T4.staff_name = #{salaryStaffName} 

        and T1.staff_id = #{staffId}


        and (T1.staff_no = #{staffNo} or T1.create_by = #{createBy}

            or T1.staff_no in

                #{item}

        )

     and T1.release_flag = #{releaseFlag}
     and T1.branch_name = #{branchName}


    order by ISNULL(T1.submit_time) desc, T1.submit_time desc, T1.distribution_time desc

with recursive tb (id, parent_id, project_id, project_type, task_no, task_name, app_name, distribution_time, staff_id, staff_no, participation_methods, description, link_url, qr_code,
    submit_time, spend_time, working_hours, paid_hours, month, tag_no, branch_name, inner_branch, release_flag, release_tag, release_attachment, deploy_url, file_url, upload_status, complete_instructions, stage, status,
    cycle_type, distribute_status, create_by, create_time, update_by, update_time, del_flag, staff_name, createByName, project_name, project_source) as
(
    select T1.*, T2.staff_name, T3.nick_name as createByName, T4.project_name, T4.project_source from wh_task T1
    left join wh_staff T2 on T1.staff_id = T2.id
    left join sys_user T3 on T1.create_by = T3.user_name and T3.del_flag = '0'
    left join wh_project T4 on T1.project_id = T4.id
    where T1.del_flag = '0'

        and T1.staff_id in

            #{staffId}



        and T1.id = #{id}


    UNION DISTINCT

    select T1.*, T2.staff_name, T3.nick_name as createByName, T4.project_name, T4.project_source from wh_task T1
    left join wh_staff T2 on T1.staff_id = T2.id
    left join sys_user T3 on T1.create_by = T3.user_name and T3.del_flag = '0'
    left join wh_project T4 on T1.project_id = T4.id
    inner join tb t on T1.parent_id = t.id
    where T1.del_flag = '0'
)
select * from tb

    tb.del_flag = '0'
     and tb.parent_id = #{parentId}
     and DATE_FORMAT(tb.distribution_time, '%Y-%m') = #{distributeMonth} 
     and tb.project_id = #{projectId}
     and tb.app_name like concat('%', #{appName}, '%')
     and tb.task_no like concat('%', #{taskNo}, '%')
     and tb.task_name like concat('%', #{taskName}, '%')
     and tb.createByName like concat('%', #{createByName}, '%' )
     and tb.distribution_time = #{distributionTime}
     and tb.staff_name = #{staffName}
     and tb.participation_methods = #{participationMethods}
     and tb.month = #{month}
     and tb.project_source = #{projectSource}
     and tb.distribute_status = #{distributeStatus}
     and tb.status = #{status}

with recursive tb (id, staff_id, parent_id, participation_methods) as ( select id, staff_id, parent_id, participation_methods from wh_task where id = #{id}
union all
select c.id, c.staff_id, c.parent_id, c.participation_methods from wh_task c inner join tb t on c.id = t.parent_id where c.del_flag = '0' ) select * from tb;

mapper.update:
update wh_task
parent_id = #{parentId}, project_id = #{projectId}, project_type = #{projectType}, task_no = #{taskNo}, task_name = #{taskName}, app_name = #{appName}, distribution_time = #{distributionTime}, staff_id = #{staffId}, staff_no = #{staffNo}, participation_methods = #{participationMethods}, description = #{description}, link_url = #{linkUrl}, qr_code = #{qrCode}, submit_time = #{submitTime}, spend_time = #{spendTime}, working_hours = #{workingHours}, paid_hours = #{paidHours}, month = #{month}, tag_no = #{tagNo}, branch_name = #{branchName}, inner_branch = #{innerBranch}, release_flag = #{releaseFlag}, release_tag = #{releaseTag}, release_attachment = #{releaseAttachment}, deploy_url = #{deployUrl}, file_url = #{fileUrl}, upload_status = #{uploadStatus}, complete_instructions = #{completeInstructions}, stage = #{stage}, status = #{status}, cycle_type = #{cycleType}, distribute_status = #{distributeStatus}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where id = #{id}

mapper.select:
select * from wh_task
where del_flag = 0
  and status = 1

    and project_id = #{projectId}


    and branch_name = #{branchName}

order by submit_time desc limit 1

select * from wh_task
where del_flag = 0
and status = 0
and distribute_status != 0
and participation_methods = 3

    and project_id = #{projectId}


    and branch_name = #{branchName}


    and staff_id = #{staffId}

select *
from wh_task
where del_flag = '0'
    and staff_id = #{staffId}

    and status = #{status}


    and stage = #{stage}


    and distribute_status = #{distributeStatus}


    and project_id = #{projectId}


    and id <> #{id}
